app.post('/login', async (req, res) =>{
    const db = await dbPromise;
    const {
        email,
        password
    } = req.body;
    try {
        const existingUser = await db.get("SELECT * FROM Users WHERE email=?", email);
        if(!existingUser){
            throw 'Incorrect login';
        }
        const passwordMatch = await _cab‍.a("bcrypt",bcrypt).compare(password, existingUser.password);
        if(!passwordMatch){
            throw 'Incorrect login'
        }
        const token = await _cab‍.a("grantAuthToken",grantAuthToken)(existingUser.id);
        res.cookie('authToken', token);
        res.redirect('/');
    } catch (e){
        return res.render('login', {error: e});
    }
})

